package com.quicktvui.sample;import com.quicktvui.sample.base.BaseApplication;import com.quicktvui.sdk.core.EsData;import com.quicktvui.sdk.core.EsManager;import com.quicktvui.sdk.core.InitConfig;import com.quicktvui.sdk.core.callback.EsAppLifeCallbackImpl;import com.sunrain.toolkit.utils.ToastUtils;/** * <br> * * <br> */public class SampleApplication extends BaseApplication {    @Override    public void onCreate() {        super.onCreate();        EsManager.get().init(this, InitConfig.getDefault()                .setChannel("debug")                .setDebug(BuildConfig.DEBUG)                .setAppLifeCallback(new EsAppLifeCallbackImpl(){                    @Override                    public void onEsAppCreate(EsData data) {                        ToastUtils.showShort(data.getEsPackage() + " open");                        super.onEsAppCreate(data);                    }                    @Override                    public void onEsAppStart(EsData data) {                        ToastUtils.showShort(data.getEsPackage() + " start");                        super.onEsAppStart(data);                    }                    @Override                    public void onEsAppStop(EsData data) {                        ToastUtils.showShort(data.getEsPackage() + " stop");                        super.onEsAppStop(data);                    }                    @Override                    public void onEsAppDestroy(EsData data) {                        ToastUtils.showShort(data.getEsPackage() + " close");                        super.onEsAppDestroy(data);                    }                }));    }}