package com.quicktvui.sample.ui;import android.content.Context;import android.graphics.Color;import android.graphics.Rect;import android.os.Build;import android.util.AttributeSet;import android.widget.Button;/** * <br> * * <br> */public class FocusButton extends Button {    public FocusButton(Context context, AttributeSet attrs) {        super(context, attrs);    }    public FocusButton(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    @Override    protected void onFocusChanged(boolean focused, int direction, Rect previouslyFocusedRect) {        super.onFocusChanged(focused, direction, previouslyFocusedRect);        setTextColor(focused ? Color.BLUE : Color.BLACK);    }}