package com.quicktvui.sdk.core.adapter;import com.quicktvui.sdk.core.EsData;import com.quicktvui.sdk.core.internal.loader.ApiRpkLoader;import com.quicktvui.sdk.core.internal.loader.AssetsRpkLoader;import com.quicktvui.sdk.core.internal.loader.DebugRpkLoader;import com.quicktvui.sdk.core.internal.loader.FileRpkLoader;import com.quicktvui.sdk.core.internal.loader.HttpRpkLoader;import com.quicktvui.sdk.core.internal.loader.IRpkLoader;import com.quicktvui.sdk.core.internal.loader.NexusRpkLoader;/** * <br> * * <br> */public class DefaultRpkLoaderAdapter {    public IRpkLoader createFileRpkLoader(EsData data) {        return new FileRpkLoader(data);    }    public IRpkLoader createAssetsRpkLoader(EsData data) {        return new AssetsRpkLoader(data);    }    public IRpkLoader createDebugRpkLoader(EsData data) {        return new DebugRpkLoader(data);    }    public IRpkLoader createHttpRpkLoader(EsData data) {        return new HttpRpkLoader(data);    }    public IRpkLoader createNexusRpkLoader(EsData data) {        return new NexusRpkLoader(data);    }    public IRpkLoader createApiRpkLoader(EsData data) {        return new ApiRpkLoader(data);    }}