package com.quicktvui.sdk.core.adapter;import com.sunrain.toolkit.utils.net.HttpRequest;import com.tencent.mtt.hippy.adapter.http.DefaultHttpAdapter;import com.tencent.mtt.hippy.adapter.http.HippyHttpRequest;import java.net.HttpURLConnection;import java.net.URL;/** * <br> * * <br> */public class HttpsAdapter extends DefaultHttpAdapter {    @Override    protected HttpURLConnection createUrlConnection(HippyHttpRequest hippyHttpRequest, URL url) throws Exception{        HttpURLConnection connection = super.createUrlConnection(hippyHttpRequest, url);        HttpRequest.trustAllCerts(connection);        HttpRequest.trustAllHosts(connection);        return connection;    }}