package com.quicktvui.sdk.core.jsview;import android.content.Context;import com.quicktvui.sdk.annotations.ESKitAutoRegister;import com.quicktvui.sdk.base.EsPromise;import com.quicktvui.sdk.base.IEsInfo;import com.quicktvui.sdk.base.args.EsMap;import com.quicktvui.sdk.base.module.IEsModule;/** * <br> * * <br> */@ESKitAutoRegisterpublic class SlotModule implements IEsModule, IEsInfo {    public static Runnable sBackPressCallback;    @Override    public void getEsInfo(EsPromise promise) {        EsMap map = new EsMap();        map.pushInt(IEsInfo.ES_PROP_INFO_VERSION, 1);        promise.resolve(map);    }    @Override    public void init(Context context) {    }    public void invokeDefaultBackPressHandler() {        if (sBackPressCallback != null) {            sBackPressCallback.run();        }    }    @Override    public void destroy() {    }}