package com.quicktvui.support.core;import android.content.Context;import android.os.Bundle;import com.quicktvui.sdk.annotations.ESKitAutoInit;import com.quicktvui.sdk.base.IEsAutoInit;import com.quicktvui.support.core.module.develop.AndroidDevelopManager;import com.quicktvui.support.core.module.device.AndroidDeviceManager;import com.quicktvui.support.core.module.mmap.MMapManager;import com.quicktvui.support.core.module.power.AndroidPowerManager;import com.quicktvui.support.core.module.storage.AndroidStorageManager;import com.quicktvui.support.core.module.usb.AndroidUsbDeviceManager;/** * <br> * * <br> */@ESKitAutoInitpublic class AutoInit implements IEsAutoInit {    @Override    public void init(Context context, Bundle params) {        AndroidDevelopManager.getInstance().init(context);        AndroidDeviceManager.getInstance().init(context);        MMapManager.getInstance().init(context);        AndroidPowerManager.getInstance().init(context);        AndroidStorageManager.getInstance().init(context);        AndroidUsbDeviceManager.getInstance().init(context);    }}